parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "selected_cards": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "The story cards selected by the user to inspire the story.",
      "minItems": 1
    },
    "story_plot": {
      "type": "string",
      "description": "The full story plot, weaving the selected cards into an exciting tale with an opening scene, main challenge, fun moments, clues, a surprise, a lesson, and a happy ending."
    },
    "before_story": {
      "type": "string",
      "description": "A brief description of what was happening just before the story begins."
    },
    "after_story": {
      "type": "string",
      "description": "A brief description of what happens after the story ends."
    }
  },
  "required": ["selected_cards", "story_plot", "before_story", "after_story"]
}

parameters.text:
