parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "language": {
      "type": "string",
      "description": "The language the story will be told in (e.g., English, Spanish)."
    },
    "story_type": {
      "type": "string",
      "description": "The type of story (e.g., magical adventure, true story, bedtime tale)."
    },
    "audience": {
      "type": "string",
      "description": "Who the story is for (e.g., little kids, bigger kids, grown-ups)."
    },
    "main_feeling": {
      "type": "string",
      "description": "The main feeling the story will give (e.g., happy, excited, cozy)."
    },
    "big_idea": {
      "type": "string",
      "description": "The important idea of the story (e.g., friendship, bravery, kindness)."
    },
    "telling_style": {
      "type": "string",
      "description": "How the story will be told (e.g., silly, rhyming, exciting words)."
    },
    "genre": {
      "type": "string",
      "description": "The story’s main flavor (e.g., fantasy, adventure, real life)."
    },
    "card": {
      "type": "object",
      "description": "Which card you pick and how will it build our story",
      "properties": {
        "card_title": {
          "type": "string",
          "description": "Title of the card"
        },
        "card_description": {
          "type": "string",
          "description": "Long description of how this card will build the story"
        }
      }
    }
  },
  "required": ["language", "story_type", "audience", "main_feeling", "big_idea", "telling_style", "genre", "card"]
}

{
  "type": "object",
  "properties": {
    "characters": {
      "type": "array",
      "minItems": 1,
      "maxItems": 7,
      "items": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "description": "A real name or a fun nickname."
          },
          "appearance": {
            "type": "string",
            "description": "What they look like—hair, clothes, special features."
          },
          "what_they_do_or_like": {
            "type": "string",
            "description": "Their favorite activity or hobby."
          },
          "family_or_friends": {
            "type": "string",
            "description": "Who is important in their life—siblings, pets, friends."
          },
          "quirks_or_habits": {
            "type": "string",
            "description": "Funny or unique traits that make them special."
          },
          "special_skill_or_talent": {
            "type": "string",
            "description": "Something they’re really good at."
          },
          "goal_or_dream": {
            "type": "string",
            "description": "What they want more than anything."
          }
        },
        "required": [
          "name",
          "appearance",
          "what_they_do_or_like",
          "family_or_friends",
          "quirks_or_habits",
          "special_skill_or_talent",
          "goal_or_dream"
        ]
      }
    }
  },
  "required": ["characters"]
}

