{
  "type": "object",
  "properties": {
    "story_type": {
      "type": "string",
      "description": "What kind of story it is (e.g., picture book, adventure, play)."
    },
    "big_idea": {
      "type": "string",
      "description": "The one big, true idea of the story."
    },
    "reason_for_story": {
      "type": "string",
      "description": "Why you are telling this story."
    },
    "feelings": {
      "type": "array",
      "items": {
        "type": "string"
      },
