      "items": {
        "type": "string"
      },
      "description": "Deeper thoughts hidden in the story."
    },
    "big_questions": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Questions to make kids think after the story."
    },
    "fun_way_to_tell_it": {
      "type": "string",
      "description": "A new, fun way to tell the story."
    }
  },
  "required": ["story_type", "big_idea", "reason_for_story", "feelings", "smart_ideas", "big_questions", "fun_way_to_tell_it"]
}

